mapper.select:
	SELECT *
	FROM MEMBER
	WHERE USER_ID = #{userId} 
	AND USER_PWD = #{user_pwd}
	AND STATUS = 'Y'

SELECT *
FROM MEMBER
WHERE USER_ID = #{userId} 
AND USER_NAME = #{userName} 
AND USER_PHONE = #{userPhone} 
AND STATUS = 'Y'

mapper.update:
UPDATE MEMBER 
SET USER_PWD = #{userPwd}
WHERE USER_ID = #{userId}
AND STATUS = 'Y'
